import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductDisplay.css';
import { ShopContext } from '../../Context/ShopContext';
import closebutton from '../Assets/closebutton.png';

export const ProductDisplay = (props) => {
  const { product } = props;
  const { addToCart } = useContext(ShopContext);
  const [selectedWear, setSelectedWear] = useState('Field Tested');
  const [displayPrice, setDisplayPrice] = useState(product.knife_price);
  const [priceDifference, setPriceDifference] = useState(0); // Initial difference for 'Field Tested'

  const handleWearChange = (wear) => {
    setSelectedWear(wear);
    let modifier;
    switch(wear) {
      case 'Factory New':
        modifier = 1.20;
        break;
      case 'Minimal Wear':
        modifier = 1.10;
        break;
      case 'Field Tested':
        modifier = 1.00;
        break;
      case 'Well-Worn':
        modifier = 0.95;
        break;
      case 'Battle-Scarred':
        modifier = 0.90;
        break;
      default:
        modifier = 1.00;
        break;
    }
    const newPrice = product.knife_price * modifier;
    setPriceDifference(newPrice - product.knife_price); // Calculate the difference from the base price
    setDisplayPrice(newPrice.toFixed(2));
  };

  return (
    <div className="product-display">
      <div className="product-display-left">
        <div className="product-display-img">
          <img className="product-display-main-img" src={product.image} alt="" />
        </div>
      </div>
      <div className="product-display-right">
        <div className="product-header">
          <h1>{`${product.name}`}</h1>
          <Link to="/knives" className="close-button">
            <img src={closebutton} width="30" height="30" alt="" />
          </Link>
        </div>
        <div className="product-price">
          ${displayPrice}
          {priceDifference !== 0 && <span className="price-change"> ({priceDifference > 0 ? `+${priceDifference.toFixed(2)}` : priceDifference.toFixed(2)})</span>}
        </div>
        <div className="product-description">
          <h2>Description</h2>
          <p>{product.description}</p>
          <p><strong>Wear:</strong> {selectedWear}</p>
        </div>
        <div className="product-wear">
          <h2>Select Wear</h2>
          <div className="wear-options">
            {['Factory New', 'Minimal Wear', 'Field Tested', 'Well-Worn', 'Battle-Scarred'].map(wear => (
              <button
                key={wear}
                className={`wear-option ${selectedWear === wear ? 'selected' : ''}`}
                onClick={() => handleWearChange(wear)}
              >
                {wear}
              </button>
            ))}
          </div>
        </div>
        <button className="add-to-cart" onClick={() => addToCart(product.id, selectedWear, displayPrice)}>ADD TO CART</button>
      </div>
    </div>
  );
};

export default ProductDisplay;
